import React, {useReducer, useEffect} from 'react'
import Reducer from './Reducer'
import AuthContext from './AuthContext'
import axios from 'axios'

export const AuthProvider = (props) => {
    const [state,dispatch] = useReducer(Reducer, AuthContext)


    useEffect(()=>{
        const token = localStorage.getItem('token');
        dispatch({type:"FETCH_USER",payload:token.username})

        console.log("auth provider token is " + token)
        axios.post('/current_user',{ 
            headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json'}
        },token).then(res=>{
                console.log("response from /current_user is ")
                console.log(res)
            })


    },[])
    return(
        <AuthContext.Provider
            value={{state,dispatch}}
            {...props}
        />
    )
}
export default AuthProvider;
