import React, {useContext, useEffect, useState} from 'react'
import {decreaseLike,increaseCommentLike,decreaseCommentLike} from '../../actions'
import AuthContext from '../../AuthContext'
import update from 'react-addons-update'

const LikeComment = ({sub,comment_id,post_id,idx,liked}) => {
    const {state,dispatch} = useContext(AuthContext)
    const {} = state;
    const [toggleCommentLikeState,setToggleCommentLikeState] = useState(null)
    const [values,setValues] = useState({post_id,comment_id,[idx]:liked})

    useEffect(()=>{
        update(idx,
            {$set:values.idx}
            )
    })


    const handleCommentClick = () =>{

        console.log("values.idx:")
        console.log(values.idx)
        console.log("sub:")
        console.log(sub)

        setValues({
            ...values,
            [idx]: !toggleCommentLikeState
        });
        console.log(values)
        handleCommentLike(increaseCommentLike,decreaseCommentLike,values)
    }

    const handleCommentLike = async (increaseFunction,decreaseFunction,values) =>{
        console.log("values:")
        console.log(values[idx])
        if(values[idx] == true){
            console.log("increase function hit")
            await increaseFunction(values.post_id,values.comment_id)
        } else{
            console.log("decrease function hit")
            await decreaseFunction(values.post_id,values.comment_id)
        }
    }

    return (
        <div>
            <button id={sub._id} onClick={handleCommentClick}>Like Comment</button>
        </div>
    )
}

export default LikeComment
