import React, {useContext, useEffect, useState} from 'react'
import AuthContext from '../../AuthContext'
import {withRouter} from 'react-router-dom'
import {fetchAllPosts} from '../../actions'
import IndividualPost from './IndividualPost'

const IndividualPostContainer = ({match}) => {
    const {state,dispatch} = useContext(AuthContext)
    const {username,allPosts} = state

    useEffect(()=>{
        fetchAllPosts(username,dispatch)
    },[username])

    console.log(allPosts.findIndex(post => post._id = match.params.postId))
    console.log(match.params.postId)
    return (
        <div>
            <IndividualPost postSet={allPosts}/>
        </div>
    )
}

export default IndividualPostContainer
