import React, {useContext, useEffect, useState} from 'react'
import AuthContext from '../../AuthContext'
import {fetchAllPosts,postComment} from '../../actions'

const Posts = () => {
    const {state,dispatch} = useContext(AuthContext)
    const {username,allPosts} = state;
    const [comment, setComment] = useState({})
    const [comment_id, setId] = useState('')

    useEffect(() =>{
        fetchAllPosts(username,dispatch)
    },[username])


    const handleChange = e => {
        setComment({...comment, [e.target.id]:e.target.value});
        setId(e.target.id)
      }

    const handleSubmit = (comment, username,comment_id) =>{
        postComment(comment, username,comment_id)
    }

    return (
        <div>
            {allPosts && allPosts.map(post => {
                const {_id} = post;
                return(
                    <>
                        <br />
                        <div>{post._id}</div>
                        <div>{post.title}</div>
                        <div>{post.content}</div>
                        <div>Written by:{post.username}</div>
                        <br />
                        {/* <textarea id={_id} onChange={e => setComment(e.target.value)} value={comment || ''}></textarea> */}
                        <textarea id={_id} onChange={handleChange}></textarea>
                        <button  onClick={handleSubmit}>Leave a Comment</button>
                    </>
                )
            })}
        </div>
    )
}

export default Posts
