import React,{useContext,useEffect,useState} from 'react'
import AuthContext from '../../AuthContext'
import {fetchPosts} from '../../actions'
import {updatePost} from '../../actions'

const EditProfile = () => {
    const {state,dispatch} = useContext(AuthContext)
    const {username,posts} = state;
    console.log("posts:")
    console.log(posts)

    const [title,setTitle] = useState(posts.title)
    const [content,setContent] = useState('')
    const [post_id, setId] = useState('')

    useEffect(() =>{
        fetchPosts(username,dispatch)

    },[username])


    const handleTitleChange = e => {

        const { target: {value,name} } = e;
        setTitle({ [name]: value });

        console.log("title:")
        console.log(title)
    }

    const handleContentChange = e => {

        const { target: {value,name} } = e;
        setContent({ [name]: value });

        console.log(content)
    }

    const handleSubmit = async e =>{
        e.preventDefault()
        const post = {title,content,username,post_id};
        await updatePost(post)
        setTitle('')
        setContent('')
    }

    return (
        <div>
                {posts && posts.map((post,i) =>{

                return(
                <div key={post._id}>
                    <form>
                        <label>Title</label>
                            <input data-title-idx={i} key={i} type="text" name={`title_${i}`} onChange={handleTitleChange} value={title[i]} />
                        <label>Content</label>
                            <textarea data-content-idx={i} id={post._id} name={`content_${i}`} onChange={handleContentChange} value={content[i]}></textarea>
                        <button onClick={handleSubmit}>
                            Submit
                        </button>
                    </form>
                </div>
                )}
            )}
        </div>
    )
}

export default EditProfile
