    }

    const handleContentChange = e => {
        setContentKey(e.target.getAttribute('data-content-idx'))
        console.log("content_key:")
        console.log(content_key)
        setId(e.target.id);

        setContent({[content_key]:e.target.value});
        console.log(content_key)
        console.log(content)

        newContent = content[content_key]
        console.log("newContent:")
        console.log(newContent)
    }

    const handleSubmit = async e =>{
        e.preventDefault()
        const post = {title,content,username,post_id};
        await updatePost(post)
        setTitle('')
        setContent('')
    }

    return (
        <div>
                {posts && posts.map((post,i) =>{
                    const {title,content} = post;
                    const [my_title,setTitle] = useState({title})
                    const [my_content,setContent] = useState({content})

                return(
                <div key={post._id}>
                    <form>
                        <label>Title</label>
                            <input data-title-idx={i} key={i} type="text" name="title" onChange={handleTitleChange} value={my_title} />
                        <label>Content</label>
                            <textarea data-content-idx={i} id={post._id} name="content" onChange={handleContentChange} value={my_content} ></textarea>
                        <button onClick={handleSubmit}>
                            Submit
                        </button>
                    </form>
                </div>
                )}
            )}
        </div>
    )

}

export default EditProfile
