import Reducer from './Reducer'
const AuthContext = createContext({
    username:''
})
export const AuthProvider = ({children}) => {
    // const [username, setUsername] = useState(true);
    const [state,dispatch] = useReducer(Reducer, AuthContext)


    useEffect(()=>{
        console.log(state)
    })
