const Reducer = (state, { type, payload }) => {
    switch (type) {

    case "CREATE_USER":
        return { 
            ...state, 
            username: payload
        }
    case "SIGN_IN_USER":
        return { 
            ...state, 
            username: payload,
            isAuth: true
        }
    case "FETCH_USER":
        return{
            ...state,
            username
        }

    default:
        return state
    }
}

export default Reducer;
