    const handleContentChange = e => {

        const { target: {value,name} } = e;
        setContent({ [name]: value });

        console.log(content)
    }

    const handleSubmit = async e =>{
        e.preventDefault()
        const post = {title,content,username,post_id};
        await updatePost(post)
        setTitle('')
        setContent('')
    }
    return (
        <div>
                    <form>
                        <label>Title</label>
                            <input type="text" name={`title_${i}`} onChange={handleTitleChange} value={post.title} />
                        <label>Content</label>
                            <textarea id={_id} name={`content_${i}`} onChange={handleContentChange} value={content}></textarea>
                        <button onClick={handleSubmit}>
                            Submit
                        </button>
                    </form>
        </div>
    )
}

export default EditPostModal
