import {fetchFavorites,increaseLike,decreaseLike} from '../../actions'
import AuthContext from '../../AuthContext'

const LikePost = ({id,user}) => {
    const {state,dispatch,allPosts} = useContext(AuthContext)
    const {username,favorites} = state;
    const [values,setValues] = useState({id})

    useEffect(()=>{
        fetchFavorites(user,dispatch)
    },[username])

    const handleClick = async() => {
        const prevLiked = favorites.findIndex(fav => fav == id) > -1;
        console.log(prevLiked)
        if(prevLiked == true){
            await decreaseLike(id)
        } else{
            await increaseLike(id)
        }
    }


    return (
        <div>
            <button onClick={handleClick()}>Like Post</button>
        </div>
    )
}

export default LikePost
