import React,{useContext,useEffect,useState} from 'react'
import AuthContext from '../../AuthContext'
import {fetchPosts} from '../../actions'
import {updatePost} from '../../actions'
import EditPostModal from '../EditRecipeModal/EditPostModal'

const EditProfile = () => {
    const {state,dispatch} = useContext(AuthContext)
    const {username,posts} = state;
    console.log("posts:")
    console.log(posts)

    const [title,setTitle] = useState(posts)
    const [content,setContent] = useState('')
    const [post_id, setId] = useState('')

    useEffect(() =>{
        fetchPosts(username,dispatch)

    },[username])


    const handleTitleChange = e => {

        const { target: {value,name} } = e;
        setTitle({ [name]: value });

        console.log("title:")
        console.log(title)
    }

    const handleContentChange = e => {

        const { target: {value,name} } = e;
        setContent({ [name]: value });

        console.log(content)
    }

    const handleSubmit = async e =>{
        e.preventDefault()
        const post = {title,content,username,post_id};
        await updatePost(post)
        setTitle('')
        setContent('')
    }

    return (
        <div>
                {posts && posts.map(({...props}) =>{

                return(
                    <EditPostModal key={post._id} {...props} />
                )}
            )}
        </div>
    )
}

export default EditProfile
