import React, {useContext, useEffect, useState} from 'react'
import AuthContext from '../../AuthContext'
import {fetchPosts,postComment} from '../../actions'
import {withRouter} from 'react-router-dom'

const Welcome = ({history}) => {
    const {state,dispatch} = useContext(AuthContext)
    const {username,posts} = state;
    const [comment, setComment] = useState({})

    useEffect(() =>{
        fetchPosts(username,dispatch)
    },[username])

    const handleSubmit = (e, comment, username) =>{
        const {name,value} = e.target.value;
        postComment(comment, username)
    }

    return (
        <>
            <div>welcome page</div>
            <br />
            {posts && posts.map(post =>{
                return(<div key={post._id}>
                        <div>{post.title}</div>
                        <div>{post.content}</div>
                        <br />
                        <textarea name={post._id} onChange={e => setComment(e.target.value)} value={comment}></textarea>
                        <button  onClick={handleSubmit}>Leave a Comment</button>
                        <br />
                        <br />
                    </div>
                    )
            }
            )}
        </>
    )
}

export default withRouter(Welcome)
