import React,{useContext,useEffect,useState} from 'react'
import AuthContext from '../../AuthContext'
import {fetchPosts} from '../../actions'
import {createPost} from '../../actions'

const EditProfile = () => {
    const {state,dispatch} = useContext(AuthContext)
    const {username,posts} = state;
    const [title,setTitle] = useState('')
    const [content,setContent] = useState('')

    useEffect(() =>{
        fetchPosts(username,dispatch)
    },[username])


    const handleChange = e => {
        setComment({[e.target.id]:e.target.value});
    }

    const handleSubmit = async e =>{
        e.preventDefault()
        const post = {title,content,username};
        await createPost(post)
        setTitle('')
        setContent('')
    }

    return (
        <div>
                {posts && posts.map((post,i) =>{

                return(

                    <form>
                        <label>Title</label>
                            <input type="text" name="title" onChange={e => setTitle(e.target.value)} value={post.title}/>
                        <label>Content</label>
                            <textarea name="content" key={i} id={post._id} onChange={e => setContent(e.target.value)} value={post.content}></textarea>
                        <button onClick={handleSubmit}>
                            Submit
                        </button>
                    </form>

                )}
            )}
        </div>
    )
}

export default EditProfile
