import React, { Component } from 'react'
const mongoose = require('mongoose');
const User = mongoose.model('../../../../models/user');

export default class SignUp extends Component {
    constructor(props){
        super(props);
        this.state ={
            email:'',
            password:''
        }
    }

    onChange = e =>{
        const {name,value} = e.target;

        this.setState({
            [name]:value
        })
    }

    handleSubmit = e =>{
        e.preventDefault()
        const {email,password} = this.state;
        const user = new User({email,password})
        user.save()
    }

    render() {
        return (
            <form>
                <label>Email</label>
                    <input onChange={this.onChange} value={this.state.name} name="email" type="text" />
                <label>Password</label>
                    <input onChange={this.onChange} value={this.state.password} name="password" type="password" />
                <button onClick={this.handleSubmit}>Submit</button>
            </form>
        )
    }
}
