import update from 'react-addons-update'

const LikeComment = ({sub,comment_id,post_id,idx}) => {
    const {state,dispatch} = useContext(AuthContext)
    const {} = state;
    const [toggleCommentLikeState,setToggleCommentLikeState] = useState(false)
    const [values,setValues] = useState({post_id,comment_id,idx:toggleCommentLikeState})

    useEffect(()=>{
        update(idx,
            {$set:values.idx}
            )
    })


    const handleCommentClick = () =>{

        console.log("values.idx:")
        console.log(values.idx)
        console.log("sub:")
        console.log(sub)

        setValues({
            ...values,
            [idx]: !toggleCommentLikeState
        });
        console.log(values)
        // handleCommentLike(increaseCommentLike,decreaseCommentLike,post_id,comment_id,idx)
    }

    // const handleCommentLike = async (increaseFunction,decreaseFunction,idPost, idComment,index) =>{
    //     console.log("commentLikedState:")
    //     console.log(commentLikedState)
    //     if(!commentLikedState){
    //         console.log("increase function hit")
    //         await increaseFunction(idPost,idComment,index)
    //     } else{
    //         console.log("decrease function hit")
    //         await decreaseFunction(idPost,idComment,index)
    //     }
    // }

    return (
        <div>
            <button id={sub._id} onClick={handleCommentClick}>Like Comment</button>
        </div>
    )
}

export default LikeComment
