import React, {useContext, useEffect, useState} from 'react'
import AuthContext from '../../AuthContext'
import {withRouter} from 'react-router-dom'
import {fetchSpecificPost} from '../../actions'
import IndividualPost from './IndividualPost'

const IndividualPostContainer = ({match}) => {
    const {state,dispatch,individualPost} = useContext(AuthContext)
    const {username,allPosts} = state
    const postId = match.params.postId;
    useEffect(()=>{
        console.log("fetchSpecificPost hit")
        console.log(postId)
        fetchSpecificPost(postId)
        console.log(individualPost)
    },[username])
    return (
        <div>
            <IndividualPost id={match.params.postId} postSet={allPosts}/>
        </div>
    )
}

export default IndividualPostContainer
