import React, {useContext, useEffect, useState} from 'react'
import AuthContext from '../../AuthContext'
import {fetchAllPosts,postComment} from '../../actions'

const Posts = () => {
    const {state,dispatch} = useContext(AuthContext)
    const {username,allPosts} = state;
    const [comment, setComment] = useState()
    const [name, setName] = useState(document.querySelector(""))

    useEffect(() =>{
        fetchAllPosts(username,dispatch)
    },[username])

    const handleSubmit = (e, comment, username) =>{
        const {name,value} = e.target.value;
        postComment(comment, username)
    }

    return (
        <div>
            {allPosts && allPosts.map(post => {
                const {_id} = post;
                return(
                    <>
                        <br />
                        <div>{post._id}</div>
                        <div>{post.title}</div>
                        <div>{post.content}</div>
                        <div>Written by:{post.username}</div>
                        <br />
                        <textarea id={_id} onChange={e => setComment(e.target.value)} value={comment}></textarea>
                        <button  onClick={handleSubmit}>Leave a Comment</button>
                    </>
                )
            })}
        </div>
    )
}

export default Posts
