import React, {useContext, useEffect, useState} from 'react'
import {findLikeNumber,increaseCommentLike,decreaseCommentLike,fetchFavorites} from '../../actions'
import AuthContext from '../../AuthContext'
import update from 'react-addons-update'

const LikeComment = ({sub,comment_id,post_id,idx,liked}) => {
    const {state,dispatch} = useContext(AuthContext)
    const {username} = state;
    const [values,setValues] = useState({post_id,comment_id,[idx]:liked})

    useEffect(()=>{
        console.log(liked)
    },[liked])

    const handleCommentClick = () =>{
        console.log("like:")
        console.log(liked)
        setValues({
            ...values,
            [idx]:liked
        })
        findLikeNumber(post_id,comment_id)
        handleCommentLike(increaseCommentLike,decreaseCommentLike,values)
    }

    const handleCommentLike = async (increaseFunction,decreaseFunction,values,username) =>{
        console.log("values:")
        console.log(values)
        console.log(values[idx])
        if(values[idx] === true){
            console.log("increase function hit")
            await increaseFunction(values.post_id,values.comment_id,username)
        } else{
            console.log("decrease function hit")
            // await decreaseFunction(values.post_id,values.comment_id,username)
        }
        dispatch({type:"COMMENT_LIKED"})
    }

    return (
        <div>
            {!liked ? (<button id={sub._id} onClick={handleCommentClick}>Like Comment</button>) : (<button id={sub._id} onClick={handleCommentClick}>Unlike Comment</button>)}
        </div>
    )
}

export default LikeComment
