import React, {useContext, useEffect, useState} from 'react'
import {fetchFavorites,increaseCommentLike,decreaseCommentLike} from '../../actions'
import AuthContext from '../../AuthContext'
import update from 'react-addons-update'

const LikeComment = ({sub,comment_id,post_id,idx,liked}) => {
    const {state,dispatch} = useContext(AuthContext)
    const {username,favorites} = state;
    const [values,setValues] = useState({post_id,comment_id,[idx]:liked})
    let prevLiked;
    useEffect(()=>{
        console.log(liked)
        console.log(state)
        fetchFavorites(values.post_id,values.comment_id,username,dispatch)
        prevLiked = favorites.findIndex(fav => fav == comment_id) > -1;
    },[username])

    const handleCommentClick = e =>{
        const {id} = e.target;
        console.log("like comment state")
        console.log(state)
        console.log("prevLiked:")
        console.log(prevLiked)
        setValues({
            ...values,
            [idx]:prevLiked
        })
        handleCommentLike(increaseCommentLike,decreaseCommentLike,values,username,dispatch)
    }

    const handleCommentLike = async (increaseFunction,decreaseFunction,values,name,redux) =>{
        console.log("values:")
        console.log(values)
        console.log(values[idx])
        await increaseFunction(values.post_id,values.comment_id,name,redux)
        if(values[idx] === true){
            console.log("increase function hit")
        } else{
            console.log("decrease function hit")
            // await decreaseFunction(values.post_id,values.comment_id,username)
        }
        dispatch({type:"COMMENT_LIKED"})
    }

    return (
        <div>
            {!liked ? (<button id={sub._id} onClick={e => handleCommentClick(e)}>Like Comment</button>) : (<button id={sub._id} onClick={handleCommentClick}>Unlike Comment</button>)}
        </div>
    )
}

export default LikeComment
