import React,{useContext,useEffect,useState} from 'react'
import AuthContext from '../../AuthContext'
import {updatePost} from '../../actions'

const EditPostModal = ({_id,i,}) => {

    const {state,dispatch} = useContext(AuthContext)
    const {username,posts} = state;
    console.log("posts:")
    console.log(posts)

    const [title,setTitle] = useState(posts)
    const [content,setContent] = useState('')
    const [post_id, setId] = useState('')

    const handleTitleChange = e => {

        const { target: {value,name} } = e;
        setTitle({ [name]: value });

        console.log("title:")
        console.log(title)
    }

    const handleContentChange = e => {

        const { target: {value,name} } = e;
        setContent({ [name]: value });

        console.log(content)
    }

    const handleSubmit = async e =>{
        e.preventDefault()
        const post = {title,content,username,post_id};
        await updatePost(post)
        setTitle('')
        setContent('')
    }
    return (
        <div>
                    <form>
                        <label>Title</label>
                            <input type="text" name={`title_${i}`} onChange={handleTitleChange} value={title} />
                        <label>Content</label>
                            <textarea id={_id} name={`content_${i}`} onChange={handleContentChange} value={content}></textarea>
                        <button onClick={handleSubmit}>
                            Submit
                        </button>
                    </form>
        </div>
    )
}

export default EditPostModal
