import React,{useContext,useEffect,useState} from 'react'
import AuthContext from '../../AuthContext'
import {fetchPosts} from '../../actions'
import {updatePost} from '../../actions'

const EditProfile = () => {
    const {state,dispatch} = useContext(AuthContext)
    const {username,posts} = state;
    const [title,setTitle] = useState('')
    const [content,setContent] = useState('')
    const [post_id, setId] = useState('')
    const [post_key, setKey] = useState('')

    useEffect(() =>{
        fetchPosts(username,dispatch)
    },[username])


    const handleChange = e => {
        setKey(e.target.key);
        setId(e.target.id);
        setTitle({[e.target.key]:e.target.value});
        setContent({["content_" + post_key]:e.target.value});
        console.log(post_id)
        console.log(content)
    }

    const handleSubmit = async e =>{
        e.preventDefault()
        const post = {title,content,username,post_id};
        await updatePost(post)
        setTitle('')
        setContent('')
    }

    return (
        <div>
                {posts && posts.map((post,i) =>{

                return(
                <div key={post._id}>
                    <form>
                        <label>Title</label>
                            <input data-title-idx={i} key={i} type="text" name="title" onChange={e => setTitle(e.target.value)} value={post.title}/>
                        <label>Content</label>
                            <textarea data-content-idx={i} id={post._id} name="content" onChange={handleChange} ></textarea>
                        <button onClick={handleSubmit}>
                            Submit
                        </button>
                    </form>
                </div>
                )}
            )}
        </div>
    )
}

export default EditProfile
